document.getElementById("boton_validar").addEventListener("click",()=>{const e=document.getElementById("expresion_regular").value.trim(),t=document.getElementById("texto_prueba").value,n=document.getElementById("resultado");try{const o=e.replace(/^\/|\/$/g,""),r=new RegExp(o),l=r.test(t);l?(n.textContent="La expresion regular coincide con el texto ingresado.",n.style.color="#00ff00"):(n.textContent="La expresion regular no coincide con el texto ingresado.",n.style.color="#ff0000")}catch(e){n.textContent="La expresion regular es invalida.",n.style.color="#ff0000"}});